class Solution:
    def minimumPairRemoval(self, nums: List[int]) -> int:
        """
        :type nums: List[int]
        :rtype: int
        """
        def merge_sort(arr):
            if len(arr) > 1:
                mid = len(arr) // 2
                L = arr[:mid]
                R = arr[mid:]

                merge_sort(L)
                merge_sort(R)

                i = j = k = 0

                while i < len(L) and j < len(R):
                    if L[i] <= R[j]:
                        arr[k] = L[i]
                        i += 1
                    else:
                        arr[k] = R[j]
                        j += 1
                    k += 1

                while i < len(L):
                    arr[k] = L[i]
                    i += 1
                    k += 1

                while j < len(R):
                    arr[k] = R[j]
                    j += 1
                    k += 1

        def find_min_pair(nums):
            min_sum = float('inf')
            min_pair = None
            for i in range(len(nums) - 1):
                if nums[i] <= nums[i + 1]:
                    continue
                if nums[i] + nums[i + 1] < min_sum:
                    min_sum = nums[i] + nums[i + 1]
                    min_pair = (i, i + 1)
            return min_pair

        def merge_sort_and_count(nums):
            if len(nums) > 1:
                mid = len(nums) // 2
                L = nums[:mid]
                R = nums[mid:]

                merge_sort_and_count(L)
                merge_sort_and_count(R)

                i = j = k = 0

                while i < len(L) and j < len(R):
                    if L[i] <= R[j]:
                        nums[k] = L[i]
                        i += 1
                    else:
                        nums[k] = R[j]
                        j += 1
                    k += 1

                while i < len(L):
                    nums[k] = L[i]
                    i += 1
                    k += 1

                while j < len(R):
                    nums[k] = R[j]
                    j += 1
                    k += 1

            min_pair = find_min_pair(nums)
            if min_pair:
                nums[min_pair[0]] += nums[min_pair[1]]
                del nums[min_pair[1]]
                return 1 + merge_sort_and_count(nums)
            else:
                return 0

        merge_sort_and_count(nums)
        return 0

