class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation sorted in lexicographical order.
        If there are fewer than k valid alternating permutations, returns an empty list.
        """
        def is_valid_permutation(perm):
            for i in range(n - 1):
                if (perm[i] % 2 == 0 and perm[i + 1] % 2 == 0) or (perm[i] % 2 != 0 and perm[i + 1] % 2 != 0):
                    return False
            return True

        def generate_permutations(n, k):
            if n == 1:
                yield [1]
            else:
                for perm in generate_permutations(n - 1, k):
                    for i in range(n):
                        if (n % 2 == 0 and perm[i] % 2 == 0) or (n % 2 != 0 and perm[i] % 2 != 0):
                            continue
                        yield perm[:i] + [n] + perm[i:]

        permutations = list(generate_permutations(n, k))
        return permutations[k - 1] if k <= len(permutations) else []
