class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation of the first n positive integers
        sorted in lexicographical order. If there are fewer than k valid
        alternating permutations, returns an empty list.
        """
        def is_valid_permutation(perm):
            """
            Returns True if the given permutation is a valid alternating permutation,
            False otherwise.
            """
            for i in range(n - 1):
                if (perm[i] % 2 == 0 and perm[i + 1] % 2 == 0) or (perm[i] % 2 != 0 and perm[i + 1] % 2 != 0):
                    return False
            return True

        def count_valid_permutations(n):
            """
            Returns the number of valid alternating permutations of the first n
            positive integers.
            """
            count = 0
            for perm in itertools.permutations(range(1, n + 1)):
                if is_valid_permutation(perm):
                    count += 1
            return count

        if k > count_valid_permutations(n):
            return []

        def get_kth_permutation(n, k):
            """
            Returns the k-th valid alternating permutation of the first n
            positive integers sorted in lexicographical order.
            """
            perm = []
            for i in range(n):
                perm.append(i + 1)
            for i in range(n - 1, 0, -1):
                if k % 2 == 0:
                    if perm[i - 1] % 2 == 0:
                        j = i - 1
                        while j > 0 and perm[j - 1] % 2 == 0:
                            j -= 1
                        perm[i - 1], perm[j] = perm[j], perm[i - 1]
                        perm[i:] = perm[i:][::-1]
                        break
                    else:
                        k -= 1
                else:
                    if perm[i - 1] % 2 != 0:
                        j = i - 1
                        while j > 0 and perm[j - 1] % 2 != 0:
                            j -= 1
                        perm[i - 1], perm[j] = perm[j], perm[i - 1]
                        perm[i:] = perm[i:][::-1]
                        break
                    else:
                        k -= 1
            return perm

        return get_kth_permutation(n, k)
