class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def lcp(a, b):
            """Return the longest common prefix of strings a and b."""
            i = 0
            while i < len(a) and i < len(b) and a[i] == b[i]:
                i += 1
            return a[:i]

        def lcp_all(words):
            """Return a list of the longest common prefixes of all words."""
            if not words:
                return []
            lcp = words[0]
            for word in words[1:]:
                lcp = lcp if lcp else word
                lcp = lcp_all([lcp, word])[0]
            return [lcp] * len(words)

        def dfs(words, k, i, prefix, result):
            """Recursively find the longest common prefix for each index i."""
            if i == len(words):
                result.append(prefix)
                return
            for j in range(len(words)):
                if j == i:
                    continue
                if len(words[j]) == 0:
                    continue
                if not prefix:
                    dfs(words, k, i + 1, words[j], result)
                else:
                    lcp_val = lcp(words[i], words[j])
                    if lcp_val == words[i]:
                        dfs(words, k, i + 1, lcp_val, result)
                    elif lcp_val == words[j]:
                        dfs(words, k, i + 1, lcp_val, result)
                    else:
                        dfs(words, k, i + 1, prefix, result)

        result = []
        dfs(words, k, 0, "", result)
        return result
