            """Perform a depth-first search to find the longest common prefix for each index."""
            if i == len(words):
                return
            if i in memo:
                return memo[i]
            if len(words) - i < k:
                memo[i] = 0
                return 0
            prefix = lcp(words[i], words[i + 1])
            for j in range(i + 2, len(words)):
                prefix = lcp(prefix, words[j])
                if len(words) - j < k:
                    break
            memo[i] = len(prefix)
            dfs(i + 1, visited, memo)

        memo = {}
        dfs(0, set(), memo)
        return [memo[i] for i in range(len(words))]
