class Solution:
    def maxSubarrays(self, n: int, conflictingPairs: List[List[int]]) -> int:
        """
        :type n: int
        :type conflictingPairs: List[List[int]]
        :rtype: int
        """
        # Build a graph where each node is a number from 1 to n and each edge represents a conflicting pair
        graph = [[] for _ in range(n + 1)]
        for a, b in conflictingPairs:
            graph[a].append(b)
            graph[b].append(a)

        # Perform DFS to find all connected components
        visited = [False] * (n + 1)
        def dfs(node, component):
            visited[node] = True
            component.append(node)
            for neighbor in graph[node]:
                if not visited[neighbor]:
                    dfs(neighbor, component)

        components = []
        for i in range(1, n + 1):
            if not visited[i]:
                component = []
                dfs(i, component)
                components.append(component)

        # For each component, find the maximum number of subarrays that do not contain both a and b for any remaining conflicting pair
        max_subarrays = 0
        for component in components:
            # Create a mask to indicate which numbers are in the component
            mask = [0] * (n + 1)
            for num in component:
                mask[num] = 1

            # Try removing each number in the component and check if it forms a valid subarray
            for i in range(1, n + 1):
                if not mask[i]:
                    new_mask = mask[:]
                    new_mask[i] = 0
                    valid = True
                    for j in range(1, n + 1):
                        if mask[j] and (i in graph[j] or j in graph[i]):
                            valid = False
                            break
                    if valid:
                        max_subarrays = max(max_subarrays, sum(mask) - 1)

        return max_subarrays
