class Solution:
    def numOfUnplacedFruits(self, fruits: List[int], baskets: List[int]) -> int:
        """
        :type fruits: List[int]
        :type baskets: List[int]
        :rtype: int
        """
        from collections import Counter
        from itertools import accumulate
        from bisect import bisect_left

        fruits = Counter(fruits)
        baskets = sorted(baskets)
        n = len(fruits)
        m = len(baskets)

        def check(fruits, baskets, i, j):
            """
            :type fruits: List[int]
            :type baskets: List[int]
            :type i: int
            :type j: int
            :rtype: bool
            """
            if i == j:
                return True

            if fruits[baskets[i]] > 0:
                fruits[baskets[i]] -= 1
                return check(fruits, baskets, i + 1, j)
            else:
                return False

        def count_unplaced(fruits, baskets, i, j):
            """
            :type fruits: List[int]
            :type baskets: List[int]
            :type i: int
            :type j: int
            :rtype: int
            """
            if i == j:
                return 0

            if fruits[baskets[i]] > 0:
                fruits[baskets[i]] -= 1
                return count_unplaced(fruits, baskets, i + 1, j)
            else:
                return 1 + count_unplaced(fruits, baskets, i, j - 1)

        def count_unplaced_with_bisect(fruits, baskets, i, j):
            """
            :type fruits: List[int]
            :type baskets: List[int]
            :type i: int
            :type j: int
            :rtype: int
            """
            if i == j:
                return 0

            if fruits[baskets[i]] > 0:
                fruits[baskets[i]] -= 1
                return count_unplaced_with_bisect(fruits, baskets, i + 1, j)
            else:
                index = bisect_left(baskets, fruits[baskets[i - 1]] + 1)
                return 1 + count_unplaced_with_bisect(fruits, baskets, index, j)

        return count_unplaced_with_bisect(fruits, baskets, 0, m)
