                    return
                seen.add(path[0] * 100 + path[1] * 10 + path[2] % 10)
                return
            for i in range(start, len(digits)):
                if i > start and digits[i] == digits[i - 1]:
                    continue
                backtrack(i + 1, path + [digits[i]])

        seen = set()
        backtrack(0, [])
        return len(seen)
