from collections import defaultdict
from math import sqrt

def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def sieve_of_eratosthenes(n):
    """Generate a list of prime numbers up to n using the Sieve of Eratosthenes algorithm."""
    is_prime = [True] * (n + 1)
    p = 2
    while (p * p <= n):
        if (is_prime[p] == True):
            for i in range(p * p, n + 1, p):
                is_prime[i] = False
        p += 1
    return [p for p in range(2, n) if is_prime[p]]

def count_connected_graphs(N, P):
    """Count the number of connected graphs with N vertices and M edges that satisfy the condition modulo P."""
    primes = sieve_of_eratosthenes(P)
    if N % 2 != 0:
        return 0
    N = N // 2
    M = (N * (N - 1)) // 2
    mod = 10**9 + 7
    dp = [0] * (M + 1)
    dp[0] = 1
    for i in range(1, M + 1):
        dp[i] = (dp[i - 1] + dp[i - 2]) % mod
    for p in primes:
        if p > M:
            break
        dp[p] = 0
        for i in range(M, p - 1, -1):
            dp[i] = (dp[i] - dp[i - p] + mod) % mod
    return dp[M]

def main():
    """Main function to read input, call the count_connected_graphs function, and print the result."""
    import sys
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    P = int(data[1])
    print(*count_connected_graphs(N, P))

if __name__ == "__main__":
