        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def prime_factors(n):
    """Return a list of prime factors of n."""
    factors = []
    while n % 2 == 0:
        factors.append(2)
        n //= 2
    i = 3
    while i * i <= n:
        while n % i == 0:
            factors.append(i)
            n //= i
        i += 2
    if n > 2:
        factors.append(n)
    return factors

def euler_totient(n, p):
    """Calculate Euler's totient function for n modulo p."""
    factors = prime_factors(n)
    result = n
    for factor in set(factors):
        if factor == p:
            result -= result // p
        else:
            result -= result // factor
    return result

def generate_graphs(n, p):
    """Generate all undirected connected simple graphs with n vertices and M edges."""
    def dfs(graph, visited, path, start):
        visited[start] = True
        path.append(start)
        for neighbor in graph[start]:
            if not visited[neighbor]:
                dfs(graph, visited, path, neighbor)
        path.pop()

    def count_even_odd_distance(graph, start, p):
        """Count the number of vertices with even and odd shortest distance from start."""
        visited = [False] * n
        path = []
        dfs(graph, visited, path, start)
        distances = [0] * n
        distances[path[0]] = 0
        for i in range(1, len(path)):
            distances[path[i]] = distances[path[i-1]] + 1
        even_count = 0
        odd_count = 0
        for distance in distances:
            if distance % 2 == 0:
                even_count += 1
            else:
                odd_count += 1
        return even_count, odd_count

    def count_valid_graphs(n, p):
        """Count the number of valid graphs modulo p."""
        count = 0
        for M in range((n * (n - 1)) // 2 + 1):
