def simulate_blocks(N, W, blocks, queries):
    """
    Simulates the movement of blocks on a grid and answers queries about their existence at specific times.

    :param N: Number of rows in the grid.
    :param W: Number of columns in the grid.
    :param blocks: List of tuples representing the initial positions of the blocks.
    :param queries: List of tuples representing the queries, where each tuple contains a time and a block number.
    """
    grid = [[0] * W for _ in range(N)]
    for x, y in blocks:
        grid[N - y - 1][x - 1] = 1

    for t in range(1, 10**101):
        if all(grid[N - 1]):
            grid = [[0] * W for _ in range(N)]
        else:
            for i in range(N - 1):
                for j in range(W):
                    if grid[i][j] == 1 and (i == N - 2 or grid[i + 1][j] == 0):
                        grid[i][j] = 0
                        if i > 0:
                            grid[i - 1][j] = 1

    for t, block in queries:
        time = t + 0.5
        if time < 1:
            continue
        if time > 10**100:
            continue
        row = int(time) - 1
        col = int((time - 1) % 1) * (W - 1) + 1
        if row < 0 or row >= N or col < 0 or col >= W:
            print("No")
        elif grid[row][col - 1] == 1:
            print("Yes")
        else:
            print("No")

