from collections import defaultdict
from functools import lru_cache

def longest_common_subsequence(s1, s2):
    """
    Returns the length of the longest common subsequence between two strings.
    """
    m, n = len(s1), len(s2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if s1[i - 1] == s2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    return dp[m][n]

@lru_cache(None)
def count_ways(s, m, k):
    """
    Returns the number of strings of length m with a longest common subsequence of length k with s.
    """
    if m == 0:
        return 1 if k == 0 else 0

    count = 0
    for i in range(26):
        for j in range(26):
            if longest_common_subsequence(s, chr(ord('a') + i) + chr(ord('a') + j)) == k:
                count += count_ways(s + chr(ord('a') + i) + chr(ord('a') + j), m - 1, k)

    return count % 998244353

