    """
    Returns a list of integers representing the shortest palindrome path lengths from each vertex to each vertex.

    :param N: Number of vertices in the graph.
    :param edges: A list of strings representing the edges of the graph.
    :return: A list of integers where the i-th element is the shortest palindrome path length from vertex i to vertex i.
    """
    graph = [[] for _ in range(N)]
    for i in range(N):
        for j in range(N):
            if edges[i][j] != '-':
                graph[i].append((j, edges[i][j]))

    def dfs(i, path, visited):
        if i == N - 1:
            return 0 if path == path[::-1] else float('inf')

        if (i, tuple(path)) in memo:
            return memo[(i, tuple(path))]

        min_length = float('inf')
        for j, label in graph[i]:
            if j not in visited:
                visited.add(j)
                path.append(label)
                length = dfs(j, path, visited)
                if length != float('inf'):
                    min_length = min(min_length, length + 1)
                path.pop()
                visited.remove(j)

        memo[(i, tuple(path))] = min_length
        return min_length

    memo = {}
    return [dfs(i, [edges[i][i]], set([i])) for i in range(N)]
