def shortest_palindrome_path(N, edges):
    """
    Given a directed graph with N vertices and a list of edges, determine the shortest path from vertex i to vertex j
    whose concatenation of labels on the edges forms a palindrome. If no such path exists, return -1.

    :param N: Number of vertices in the graph
    :param edges: List of edges where each edge is a tuple (i, j, label) representing a directed edge from vertex i to vertex j with label
    :return: List of shortest palindrome path lengths for each pair (i, j)
    """
    graph = [[] for _ in range(N + 1)]
    for i, j, label in edges:
        graph[i].append((j, label))

    def is_palindrome(s):
        return s == s[::-1]

    def dfs(i, j, path, visited):
        if i == j:
            return 0
        if (i, j) in memo:
            return memo[(i, j)]
        if (j, i) in memo:
            return memo[(j, i)]
        if not graph[i]:
            return float('inf')
        min_length = float('inf')
        for neighbor, label in graph[i]:
            if neighbor not in visited:
                visited.add(neighbor)
                path.append(label)
                if is_palindrome(''.join(path)):
                    min_length = min(min_length, 1 + dfs(neighbor, j, path, visited))
                path.pop()
                visited.remove(neighbor)
        memo[(i, j)] = min_length
        memo[(j, i)] = min_length
        return min_length

    memo = {}
    results = [-1] * (N + 1)
    for i in range(1, N + 1):
        for j in range(1, N + 1):
            results[i] = min(results[i], dfs(i, j, [chr(97)], {i}))
    return results[1:]
