    """
    Finds the shortest palindrome path from vertex i to vertex j.
    Returns the length of the shortest path if it exists, otherwise -1.
    """
    from collections import deque
    from functools import lru_cache

    @lru_cache(None)
    def is_palindrome(s):
        """
        Checks if a string is a palindrome.
        """
        return s == s[::-1]

    @lru_cache(None)
    def bfs(start, end):
        """
        Performs a breadth-first search to find the shortest palindrome path from start to end.
        Returns the length of the shortest path if it exists, otherwise -1.
        """
        queue = deque([(start, 0)])
        visited = set()
        while queue:
            current, length = queue.popleft()
            if current == end:
                return length
            for neighbor in range(N):
                if adjacency_matrix[current-1][neighbor-1] != '-' and (neighbor, current) not in visited:
                    if is_palindrome(adjacency_matrix[current-1][neighbor-1]):
                        visited.add((neighbor, current))
                        queue.append((neighbor, length + 1))
        return -1

    for i in range(N):
        for j in range(N):
            if adjacency_matrix[i-1][j-1] != '-':
                return bfs(i, j)
    return -1

