    def dfs(node, parent):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                if dfs(neighbor, node):
                    return True
            elif neighbor != parent:
                return True
        return False

    visited = [False] * (len(graph) + 1)
    max_alkane_size = 0

    for node in range(1, len(graph) + 1):
        if not visited[node]:
            if dfs(node, -1):
                max_alkane_size = max(max_alkane_size, sum(visited) - 1)

