from collections import defaultdict

def is_alkane(edges):
    """
    Determines if a given graph is an alkane.

    :param edges: List of tuples representing the edges of the graph.
    :return: True if the graph is an alkane, False otherwise.
    """
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, parent):
        degree = 0
        for neighbor in graph[node]:
            if neighbor == parent:
                continue
            if neighbor in visited:
                return False
            visited.add(neighbor)
            if not dfs(neighbor, node):
                return False
            degree += 1
        if degree not in [1, 4]:
            return False
        return True

    visited = set()
    for node in graph:
        if node not in visited:
            visited.add(node)
            if not dfs(node, -1):
