                if 0 <= ni < H and 0 <= nj < W:
                    adj[(i, j)].append((ni, nj))
                    adj[(ni, nj)].append((i, j))

    # Priority queue for Dijkstra's algorithm
    pq = []
    visited = set()
    dist = defaultdict(lambda: float('inf'))
    dist[(0, 0)] = 0

    for i in range(H):
        for j in range(W):
            heappush(pq, (0, (i, j)))

    while pq:
        d, (i, j) = heappop(pq)
        if (i, j) in visited:
            continue
        visited.add((i, j))

        for ni, nj in adj[(i, j)]:
            if (ni, nj) in visited:
                continue

            if F[ni][nj] - F[i][j] == 1:
                new_dist = d + 1
            else:
                new_dist = d + 2

            if new_dist < dist[(ni, nj)]:
                dist[(ni, nj)] = new_dist
                heappush(pq, (new_dist, (ni, nj)))

    return dist

def main():
    """
    Reads the input, finds the minimum number of stairs needed for each query, and writes the results to stdout.
    """
    H, W, F = read_input()
    queries = []
    for _ in range(int(input().strip())):
        A, B, Y, C, D, Z = map(int, input().strip().split())
        queries.append((A - 1, B - 1, Y, C - 1, D - 1, Z))

    min_stairs = find_min_stairs(H, W, F, queries)

    for i in range(len(queries)):
        A, B, Y, C, D, Z = queries[i]
        print(min_stairs[(A, B)][(C, D)] - 1)

if __name__ == "__main__":
    main()

def solve():
    import sys
    input = sys.stdin.read
    data = input().split()
    H, W = int(data[0]), int(data[1])
    F = [[int(data[2 + i * W + j]) for j in range(W)] for i in range(H)]
    Q = int(data[2 + H * W])
    queries = []
    for i in range(Q):
        A, B, Y, C, D, Z = int(data[2 + H * W + i * 6 + 0]), int(data[2 + H * W + i * 6 + 1]), int(data[2 + H * W + i * 6 + 2]), int(data[2 + H * W + i * 6 + 3]), int(data[2 + H * W + i * 6 + 4]), int(data[2 + H * W + i * 6 + 5])
        queries.append((A, B, Y, C, D, Z))

    def min_stairs(A, B, Y, C, D, Z):
        F_A, F_B = F[A - 1][B - 1], F[C - 1][D - 1]
        if F_A == F_B:
            return min(F_A - Y, Z - F_B)
        else:
            return min(F_A - Y, F_B - Z) + min(F_A - Y, F_B - Z)

    for A, B, Y, C, D, Z in queries:
        print(min_stairs(A, B, Y, C, D, Z))

