from collections import defaultdict

def min_cost_to_reach_vertex(N, M, X, edges):
    """
    Computes the minimum total cost to reach vertex N from vertex 1 in a directed graph.

    Parameters:
    N (int): Number of vertices in the graph.
    M (int): Number of edges in the graph.
    X (int): Cost to reverse all edges.
    edges (list of tuples): List of edges in the graph, where each edge is represented as (u, v).

    Returns:
    int: Minimum total cost to reach vertex N.
    """
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)

    visited = [False] * (N + 1)
    cost = [0] * (N + 1)
    cost[1] = 0

    while not visited[N]:
        min_cost = float('inf')
        min_vertex = -1

        for vertex in range(1, N + 1):
            if not visited[vertex] and cost[vertex] < min_cost:
                min_cost = cost[vertex]
                min_vertex = vertex

        if min_vertex == -1:
            return float('inf')

        visited[min_vertex] = True

        for neighbor in graph[min_vertex]:
            if not visited[neighbor]:
                cost[neighbor] = min(cost[neighbor], cost[min_vertex] + 1)

        if min_vertex in graph:
            for neighbor in graph[min_vertex]:
                if not visited[neighbor]:
                    cost[neighbor] = min(cost[neighbor], cost[min_vertex] + X)

    return cost[N]
