def min_cost_to_reach_vertex(N, M, X, edges):
    """
    Finds the minimum total cost required to reach vertex N from vertex 1.

    Parameters:
    N (int): Number of vertices.
    M (int): Number of edges.
    X (int): Cost to reverse all edges.
    edges (list of tuples): List of edges where each edge is represented as (u, v).

    Returns:
    int: Minimum total cost to reach vertex N.
    """
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)

    visited = [False] * (N + 1)
    visited[1] = True

    cost = 0

    while not visited[N]:
        min_cost = float('inf')
        next_vertex = None

        for u in range(1, N + 1):
            if not visited[u]:
                for v in graph[u]:
                    if not visited[v]:
                        cost_to_reach_v = 1
                        if (u, v) in edges:
                            cost_to_reach_v += 1
                        if (v, u) in edges:
                            cost_to_reach_v += 1
                        if cost_to_reach_v < min_cost:
                            min_cost = cost_to_reach_v
                            next_vertex = u

        if next_vertex is None:
            break

        visited[next_vertex] = True
        cost += min_cost

    if not visited[N]:
        cost += X

    return cost

