def min_xor_path(n, edges):
    """
    Finds the minimum XOR of the labels of the edges on the path from vertex 1 to vertex n
    among all simple paths in a simple connected undirected graph.

    :param n: Number of vertices in the graph
    :param edges: List of tuples (u, v, w) representing edges with labels w
    :return: Minimum XOR of edge labels on the path from vertex 1 to vertex n
    """
    graph = [[] for _ in range(n + 1)]
    for u, v, w in edges:
        graph[u].append((v, w))
        graph[v].append((u, w))

    def dfs(node, parent, path, xor_val):
        if node == n:
            return xor_val

        min_xor = float('inf')
        for neighbor, weight in graph[node]:
            if neighbor != parent:
                min_xor = min(min_xor, dfs(neighbor, node, path + [weight], xor_val ^ weight))

        return min_xor

    return dfs(1, None, [0], 0)
