def solve(N, M, X, Y, Z):
    """
    Determines if there exists a good sequence A and returns it if it exists.
    If no good sequence exists, returns -1.
    """
    from itertools import product
    from functools import reduce
    from operator import xor

    def is_good(A):
        return all(reduce(xor, (A[X[i] - 1], A[Y[i] - 1])) == Z[i] for i in range(M))

    for A in product(range(N + 1), repeat=N):
        if is_good(A):
            return A
    return -1
