from collections import defaultdict

def is_decomposable(N, K, edges):
    """
    Determines if a tree with NK vertices can be decomposed into N paths, each of length K.

    :param N: Number of vertices in the tree.
    :param K: Length of each path.
    :param edges: List of edges in the tree.
    :return: True if the tree can be decomposed, False otherwise.
    """
    # Build the adjacency list representation of the tree
    adj_list = defaultdict(list)
    for u, v in edges:
        adj_list[u].append(v)
        adj_list[v].append(u)

    # Check if the tree is connected
    visited = set()
    def dfs(node):
        if node in visited:
            return False
        visited.add(node)
        for neighbor in adj_list[node]:
            if not dfs(neighbor):
                return False
        return True
    if not dfs(1):
        return False

    # Check if the tree can be decomposed into N paths of length K
    path_count = 0
    def dfs_path(node, path):
        nonlocal path_count
        if path_count == N:
            return True
        if len(path) == K:
            path_count += 1
            path = []
        if path_count > N:
            return False
        if node in visited:
            return False
        visited.add(node)
        for neighbor in adj_list[node]:
            if neighbor not in path:
                if dfs_path(neighbor, path + [neighbor]):
                    return True
        return False
    for i in range(1, N*K+1):
        if not dfs_path(i, [i]):
            return False
    return True

