    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, path, path_index, visited):
        if path_index == K:
            return True
        if path_index > K:
            return False
        if path_index == 0:
            path.append(node)
            visited.add(node)
            for neighbor in graph[node]:
                if neighbor not in visited:
                    if dfs(neighbor, path, path_index + 1, visited):
                        return True
                    path.pop()
            visited.remove(node)
            return False
        else:
            for neighbor in graph[node]:
                if neighbor not in visited:
                    if neighbor not in path[:path_index]:
                        path.append(neighbor)
                        visited.add(neighbor)
                        if dfs(neighbor, path, path_index + 1, visited):
                            return True
                        path.pop()
            visited.remove(node)
            return False

    for _ in range(N):
        path = []
        visited = set()
        if not dfs(1, path, 0, visited):
            return False
        if len(path) != N * K:
            return False
    return True

