def simulate_smoke(N, R, C, S):
    """
    Simulates the movement of smoke on an infinitely large grid based on the given rules.

    Parameters:
    N (int): The number of time steps.
    R (int): Takahashi's row position.
    C (int): Takahashi's column position.
    S (str): A string of N characters representing the wind directions.

    Returns:
    str: A string of N characters representing the presence of smoke at each time step.
    """
    grid = [[0] * (2 * N + 1) for _ in range(2 * N + 1)]
    grid[R + N][C + N] = 1  # Campfire at (0,0)

    for t in range(N):
        new_grid = [[0] * (2 * N + 1) for _ in range(2 * N + 1)]
        for r in range(2 * N + 1):
            for c in range(2 * N + 1):
                if grid[r][c] == 1:
                    if S[t] == 'N':
                        new_grid[r - 1][c] = 1
                    elif S[t] == 'W':
                        new_grid[r][c - 1] = 1
                    elif S[t] == 'S':
                        new_grid[r + 1][c] = 1
                    elif S[t] == 'E':
                        new_grid[r][c + 1] = 1
        grid = new_grid

    return ''.join(['1' if grid[R + N][C + N] == 1 else '0' for _ in range(N)])
