from collections import defaultdict

def solve(n, edges):
    """
    Solves the Odd Cycle Game for a given graph.

    Parameters:
    n (int): Number of vertices in the graph.
    edges (list of tuples): List of edges in the graph.

    Returns:
    str: "Aoki" if Aoki wins, "Takahashi" if Takahashi wins.
    """
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, parent):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                if dfs(neighbor, node):
                    return True
            elif neighbor != parent:
                return True
        return False

    visited = [False] * (n + 1)
    for i in range(1, n + 1):
        if not visited[i]:
            if dfs(i, -1):
                return "Aoki"
    return "Takahashi"
