def min_edges_to_forest(N, edges):
    """
    Returns the minimum number of edges to delete from a graph with N vertices and M edges
    to make it a forest.

    :param N: Number of vertices in the graph
    :param edges: List of tuples representing the edges in the graph
    :return: Minimum number of edges to delete
    """
    from collections import defaultdict
    from collections import deque

    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * (N + 1)
    parent = [-1] * (N + 1)
    queue = deque()

    def bfs(node):
        queue.append(node)
        visited[node] = True
        while queue:
            current = queue.popleft()
            for neighbor in graph[current]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    parent[neighbor] = current
                    queue.append(neighbor)

    for i in range(1, N + 1):
        if not visited[i]:
            bfs(i)

    def find_cycle(node):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                if find_cycle(neighbor):
                    return True
                elif parent[neighbor] != -1:
                    return True
        return False

    return sum(not find_cycle(i) for i in range(1, N + 1))
