def solve_problem(N, M, operations):
    """
    Solves the problem of minimizing the cost of operations to make x_1 = x_2 = ... = x_N = 1.

    Parameters:
    N (int): The length of the sequence.
    M (int): The number of operations.
    operations (list of tuples): Each tuple (L_i, R_i) represents an operation.

    Returns:
    str: A string representing the sequence and the total cost of operations.
    """
    x = [0] * N
    total_cost = 0

    for L, R in operations:
        if L == R:
            if x[L - 1] == 0:
                x[L - 1] = 1
                total_cost += 1
        else:
            for i in range(L - 1, R):
                if x[i] == 0:
                    x[i] = 1
                    total_cost += 1

    if x[0] == 1 and x[-1] == 1:
        return f"{total_cost}\n{total_cost * '0'} {total_cost * '1'}"
    else:
        return "-1"
