    for i in range(N):
        if A[i] != B[i]:
            if B[i] == 1:
                if A[i] == 0:
                    if i == 0:
                        return -1
                    for j in range(i):
                        if A[j] == 1:
                            A[j] = 0
                            A[i] = 1
                            operations += 1
                            break
                else:
                    if i == N - 1:
                        return -1
                    for j in range(i + 1, N):
                        if A[j] == 1:
                            A[j] = 0
                            A[i] = 1
                            operations += 1
