from collections import defaultdict
from itertools import permutations

def weighted_tree_distance(A, queries):
    """
    Computes the sum of weighted distances between specified vertices in all possible weighted trees.

    :param A: List[int] - The weights of the edges in the tree.
    :param queries: List[Tuple[int, int]] - The queries to be processed.
    :return: List[int] - The results of the queries modulo 998244353.
    """
    N = len(A)
    tree = defaultdict(list)
    for i in range(1, N):
        tree[A[i-1]].append(i)

    def dfs(node, parent, path):
        if node == 1:
            return 0
        return sum(dfs(child, node, path + [child]) for child in tree[node] if child != parent) % 998244353

    def get_distances(node, parent, path, distances):
        if node == 1:
            return distances
        return get_distances(tree[node][0], node, path + [node], distances + [dfs(node, parent, path + [node]) % 998244353])

    results = []
    for u, v in queries:
        distances = get_distances(u, 0, [u], [0])
        result = 0
        for P in permutations(range(1, N)):
            tree = defaultdict(list)
            for i in range(1, N):
                tree[A[i-1]].append(i)
            for i in range(1, N):
                if P[i-1] == i:
                    continue
                tree[P[i-1]].append(i)
            for i in range(1, N):
                if P[i-1] == i:
                    continue
                distances = get_distances(i, 0, [i], [0])
                for j in range(1, N):
                    if P[j-1] == j:
                        continue
                    distances = get_distances(j, 0, [j], [0])
                    result = (result + distances[u-1] + distances[v-1] - distances[i-1] - distances[j-1]) % 998244353
        results.append(result)
    return results
