def is_valid_edge(H, W, i, j, direction):
    """Checks if a line segment of a given direction exists in a given cell."""
    if direction == 'right':
        if j == W - 1:
            return is_valid_edge(H, W, i, 0, 'right')
        else:
            return is_valid_edge(H, W, i, j + 1, 'right')
    elif direction == 'left':
        if j == 0:
            return is_valid_edge(H, W, i, W - 1, 'left')
        else:
            return is_valid_edge(H, W, i, j - 1, 'left')
    elif direction == 'bottom':
        if i == H - 1:
            return is_valid_edge(H, W, 0, j, 'bottom')
        else:
            return is_valid_edge(H, W, i + 1, j, 'bottom')
    elif direction == 'top':
