from collections import defaultdict
from functools import lru_cache
from typing import List

MOD = 998244353

def count_ways(H: int, W: int, S: List[str]) -> int:
    """
    Counts the number of valid placements of tiles on a grid such that
    the line segments on the tiles have no dead ends when viewed as a torus.

    :param H: Number of rows in the grid.
    :param W: Number of columns in the grid.
    :param S: List of strings representing the grid.
    :return: Number of valid placements modulo 998244353.
    """
    @lru_cache(None)
    def dfs(i: int, j: int, prev: str, prev_type: str) -> int:
        if i == H:
            return 1 if prev_type == 'A' else 0
        if j == W:
            return dfs(i + 1, 0, prev, prev_type)

        count = 0
        if prev_type == 'A':
            if S[i][j] == 'A':
                count = (count + dfs(i, j + 1, 'A', 'A')) % MOD
            elif S[i][j] == 'B':
                count = (count + dfs(i, j + 1, 'A', 'B')) % MOD
        elif prev_type == 'B':
            if S[i][j] == 'A':
                count = (count + dfs(i, j + 1, 'B', 'A')) % MOD
            elif S[i][j] == 'B':
                count = (count + dfs(i, j + 1, 'B', 'B')) % MOD

        if i == 0 or j == 0 or i == H - 1 or j == W - 1:
            if (prev_type == 'A' and (S[i][j] == 'A' or S[i][j] == 'B')) or \
               (prev_type == 'B' and (S[i][j] == 'A' or S[i][j] == 'B')):
                count = (count + dfs(i, j + 1, 'A', 'A')) % MOD
                count = (count + dfs(i, j + 1, 'B', 'B')) % MOD

        return count

    return dfs(0, 0, 'A', 'A') % MOD

