from functools import lru_cache
from itertools import product

MOD = 998244353

def solve(H, W, S):
    """
    Solves the problem for the given grid and returns the number of valid placements.

    :param H: Number of rows in the grid.
    :param W: Number of columns in the grid.
    :param S: List of strings representing the grid.
    :return: Number of valid placements modulo 998244353.
    """
    @lru_cache(None)
    def is_valid_placement(i, j, a, b):
        """
        Checks if the placement of a Type-A tile at (i, j) and a Type-B tile at (i, (j+1) % W) is valid.

        :param i: Row index of the tile.
        :param j: Column index of the tile.
        :param a: Number of Type-A tiles.
        :param b: Number of Type-B tiles.
        :return: True if the placement is valid, False otherwise.
        """
        if a == 0 and b == 0:
            return True
        if a == 0 or b == 0:
            return False

        if i == 0 and j == 0:
            return is_valid_placement(i, j+1, a, b-1) or is_valid_placement(i+1, j, a-1, b)
        elif i == 0 and j == W-1:
            return is_valid_placement(i, j-1, a, b-1) or is_valid_placement(i+1, j, a-1, b)
        elif i == H-1 and j == 0:
            return is_valid_placement(i, j+1, a, b-1) or is_valid_placement(i-1, j, a-1, b)
        elif i == H-1 and j == W-1:
            return is_valid_placement(i, j-1, a, b-1) or is_valid_placement(i-1, j, a-1, b)
        elif i == 0:
            return is_valid_placement(i, j+1, a, b-1) or is_valid_placement(i+1, j, a-1, b) or is_valid_placement(i, j-1, a, b-1) or is_valid_placement(i+1, j-1, a-1, b)
        elif i == H-1:
            return is_valid_placement(i, j+1, a, b-1) or is_valid_placement(i-1, j, a-1, b) or is_valid_placement(i, j-1, a, b-1) or is_valid_placement(i-1, j-1, a-1, b)
        elif j == 0:
            return is_valid_placement(i, j+1, a, b-1) or is_valid_placement(i+1, j, a-1, b) or is_valid_placement(i-1, j, a-1, b) or is_valid_placement(i-1, j+1, a-1, b)
        elif j == W-1:
            return is_valid_placement(i, j-1, a, b-1) or is_valid_placement(i+1, j, a-1, b) or is_valid_placement(i-1, j, a-1, b) or is_valid_placement(i-1, j-1, a-1, b)
        else:
            return is_valid_placement(i, j+1, a, b-1) or is_valid_placement(i+1, j, a-1, b) or is_valid_placement(i-1, j, a-1, b) or is_valid_placement(i-1, j+1, a-1, b) or is_valid_placement(i+1, j-1, a-1, b)

