from collections import defaultdict

def count_strongly_connected_partitions(N, S):
    """
    Counts the number of ways to partition the vertices into N pairs such that the final graph is strongly connected.

    :param N: Number of vertices
    :param S: A string of length 2N consisting of N Ws and N Bs
    :return: Number of strongly connected partitions modulo 998244353
    """
    graph = defaultdict(list)
    for i in range(2 * N):
        if S[i] == 'W':
            graph[i].append(i + 1)
        else:
            graph[i + 1].append(i)

    def dfs(node, visited, stack):
        visited.add(node)
        for neighbor in graph[node]:
            if neighbor not in visited:
                dfs(neighbor, visited, stack)
        stack.append(node)

    def reverse_graph():
        rev_graph = defaultdict(list)
        for u in graph:
            for v in graph[u]:
                rev_graph[v].append(u)
        return rev_graph

    def is_strongly_connected():
        visited = set()
        stack = []
        dfs(0, visited, stack)
        for i in range(2 * N):
            if i not in visited:
                return False
        rev_graph = reverse_graph()
        visited = set()
        while stack:
            node = stack.pop()
            if node not in visited:
