from collections import defaultdict, deque

def shortest_path(graph, start, end):
    """
    Returns the shortest path from start to end using BFS.
    """
    queue = deque([(start, [start])])
    visited = set()
    while queue:
        node, path = queue.popleft()
        if node == end:
            return path
        if node not in visited:
            visited.add(node)
            for neighbor in graph[node]:
                if neighbor not in visited:
                    queue.append((neighbor, path + [neighbor]))
    return None

def can_set_strengths(graph, start, end, strengths):
    """
    Determines if it is possible to set the strengths of the roads such that
    the stamina requirements of all people are satisfied.
    """
    path = shortest_path(graph, start, end)
    if not path:
        return False
    stamina = 0
    for i, node in enumerate(path):
        if i == 0 or i == len(path) - 1:
            if node != start and node != end:
                return False
        else:
            stamina += strengths[path[i-1]]
            if stamina < 0:
                return False
    return True

def solve_problem():
    """
