        start = int(data[2*M + 2*i])
        end = int(data[2*M + 2*i + 1])
        people.append((start, end))
    queries = []
    for i in range(Q):
        start = int(data[2*M + 2*M + 2*i])
        end = int(data[2*M + 2*M + 2*i + 1])
        queries.append((start, end))
    strengths = [0] * N
    for i in range(M):
        start, end = people[i]
        if can_set_strengths(graph, start, end, strengths):
            for j in range(start, end):
                strengths[j] = 1
            for j in range(end, N):
                strengths[j] = -1
            for j in range(0, start):
                strengths[j] = 1
            for j in range(N, end, -1):
                strengths[j] = -1
            break
    for start, end in queries:
        if can_set_strengths(graph, start, end, strengths):
            print("Yes")
        else:
            print("No")

solve_problem()
