class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        n = len(nums)
        dp = [0] * (n + 1)
        dp[0] = 1

        for i in range(1, n + 1):
            dp[i] = dp[i - 1] + 1
            for j in range(i):
                if nums[i - 1] >= nums[j]:
                    dp[i] = max(dp[i], dp[j] + 1)
                else:
                    dp[i] = max(dp[i], dp[j] + 1 - (nums[i - 1] - nums[j] - 1))

        return dp[-1] - k
