class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        n = len(nums)
        dp = [0] * (n + 1)
        for i in range(n):
            dp[i + 1] = dp[i] + (i - 0 + 1) * (i - 0 + 1) // 2
            for j in range(i, -1, -1):
                if nums[j] <= nums[i]:
                    dp[i + 1] -= dp[j]
                else:
                    break
            dp[i + 1] += min(k, i - j + 1)
        return dp[n]
