class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        n = len(nums)
        graph = [[] for _ in range(n)]
        for u, v, _ in edges:
            graph[u].append((v, nums[v]))
            graph[v].append((u, nums[u]))

        def dfs(node, parent):
            nonlocal longest, second_longest
            max_val = second_max_val = 0
            for child, val in graph[node]:
                if child != parent:
                    child_val = dfs(child, node)
                    if child_val > max_val:
                        second_longest = max_val
                        max_val = child_val
                    elif child_val > second_longest:
                        second_longest = child_val
            if parent == -1:
                if max_val > 0:
                    longest = max(longest, max_val + second_longest + 1)
                return max_val + 1
            if nums[node] != nums[parent]:
                if max_val > 0:
                    longest = max(longest, max_val + 1)
                return max_val + 1
            return 0

        longest = 0
        dfs(0, -1)
        return [longest, len(nums) - longest]
