class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        def dfs(node, parent):
            nonlocal max_path, min_path
            if not graph[node]:
                if nums[node] > max_path:
                    max_path = nums[node]
                    min_path = 1
                elif nums[node] == max_path:
                    min_path += 1
                return [nums[node], min_path]

            children = []
            for child in graph[node]:
                if child != parent:
                    child_path = dfs(child, node)
                    children.append(child_path)

            if not children:
                return [0, 1]

            if len(children) == 1:
                child_path = children[0]
                if nums[node] > child_path[0]:
                    return [nums[node], child_path[1] + 1]
                elif nums[node] == child_path[0]:
                    return [nums[node], child_path[1] + 1]
                else:
                    return child_path

            children.sort(reverse=True)
            if nums[node] > children[0][0]:
                return [nums[node], children[0][1] + 1]
            elif nums[node] == children[0][0]:
                return [nums[node], children[0][1] + 1]
            else:
                return children[1]

        n = len(nums)
        graph = [[] for _ in range(n)]
        for u, v, _ in edges:
            graph[u].append(v)
            graph[v].append(u)

        max_path, min_path = 0, 1
        dfs(0, -1)
        return [max_path, min_path]
