class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        graph = collections.defaultdict(list)
        for u, v, _ in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(node, parent):
            nonlocal longest, second_longest
            if not graph[node]:
                return nums[node], 0

            max_val = second_max_val = -float('inf')
            for neighbor in graph[node]:
                if neighbor == parent:
                    continue
                val, second_val = dfs(neighbor, node)
                if val > max_val:
                    second_longest = max_val
                    max_val = val
                elif val > second_max_val:
                    second_max_val = val
            if parent == -1 and max_val == second_max_val:
                return 0, 0
            if nums[node] == max_val:
                return max_val + 1, second_max_val
            return max_val, second_max_val

        longest = second_longest = 0
        dfs(0, -1)
        return [longest, second_longest]
