class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        adj = [[] for _ in range(len(nums))]
        for u, v, _ in edges:
            adj[u].append((v, nums[v]))
            adj[v].append((u, nums[u]))

        def dfs(node, parent):
            nonlocal max_path, max_path_count, max_path_count2
            max_path = max_path_count = max_path_count2 = 0
            for child, val in adj[node]:
                if child == parent:
                    continue
                path, count = dfs(child, node)
                if val != nums[node]:
                    max_path = max(max_path, path)
                    max_path_count = max(max_path_count, count)
                else:
                    max_path_count2 = max(max_path_count2, path + count)
            return max_path + 1, max_path_count + 1

        max_path, max_path_count, max_path_count2 = 0, 0, 0
        for i in range(len(nums)):
            if max_path_count2 > max_path_count:
                max_path, max_path_count = max_path_count2, max_path_count2
            if max_path_count > max_path_count2:
                max_path, max_path_count = max_path_count, max_path_count2
            max_path_count2 = 0
            dfs(i, -1)
        return [max_path, max_path_count]
