class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        graph = collections.defaultdict(list)
        for u, v, length in edges:
            graph[u].append((v, length))
            graph[v].append((u, length))

        def dfs(node, parent):
            nonlocal longest, second_longest
            if not graph[node]:
                return 0, 0

            longest_child, second_longest_child = 0, 0
            for child, length in graph[node]:
                if child == parent:
                    continue
                longest_child, second_longest_child = dfs(child, node)
                if nums[node] != nums[child]:
                    if longest_child > second_longest_child:
                        second_longest_child, longest_child = longest_child, second_longest_child
                    second_longest = max(second_longest, longest_child)
                    longest = max(longest, second_longest + length)
                else:
