        """
        def is_match(i, j):
            """
            Checks if the substring s[i:j] matches the pattern p.

            :param i: Starting index of the substring to check.
            :param j: Ending index of the substring to check (exclusive).
            :return: True if the substring matches the pattern, False otherwise.
            """
            while i < j:
                if p[i] == '*':
                    if i == 0:
                        return is_match(i + 1, j)
                    elif p[i - 1] == '*':
                        return is_match(i - 1, j)
                    else:
                        return is_match(i, j - 1)
                elif p[i] != s[j - 1]:
                    return False
                i += 1
                j -= 1
            return i == j

        n, m = len(s), len(p)
        if m != 2:
            return -1

        start, end = -1, -1
        for i in range(n):
            for j in range(i, n + 1):
                if is_match(0, j):
                    if start == -1 or j - i < end - start:
                        start, end = i, j

        return end - start if start != -1 else -1
