    def minCostGoodCaption(self, caption: str) -> str:
        """
        Returns the minimum number of operations required to make the caption a good caption.
        If it is impossible to create a good caption, returns an empty string.
        """
        n = len(caption)
        if n == 0:
            return ""

        # Create a list to store the minimum operations required for each character
        min_ops = [float('inf')] * 26

        # Iterate through the caption and calculate the minimum operations for each character
        for i in range(n):
            char = caption[i]
            prev_char = caption[i-1] if i > 0 else 'a'
            next_char = caption[(i+1) % n] if (i+1) % n < n else 'z'

            if char == prev_char:
                min_ops[ord(char) - ord('a')] = min(min_ops[ord(char) - ord('a')], min_ops[ord(prev_char) - ord('a')] + 1)
            elif char == next_char:
                min_ops[ord(char) - ord('a')] = min(min_ops[ord(char) - ord('a')], min_ops[ord(next_char) - ord('a')] + 1)
            else:
                min_ops[ord(char) - ord('a')] = min(min_ops[ord(char) - ord('a')], min(min_ops[ord(prev_char) - ord('a')] + 1, min_ops[ord(next_char) - ord('a')] + 1))

        # Find the minimum operations required to make the caption a good caption
        min_ops = min(min_ops)

        # If the minimum operations required is greater than or equal to 2, return an empty string
        if min_ops >= 2:
            return ""

        # Create a list to store the characters that need to be changed
        chars_to_change = []

        # Iterate through the caption and find the characters that need to be changed
        for i in range(n):
            char = caption[i]
            prev_char = caption[i-1] if i > 0 else 'a'
            next_char = caption[(i+1) % n] if (i+1) % n < n else 'z'

            if char == prev_char:
                if min_ops == min_ops[ord(prev_char) - ord('a')]:
                    chars_to_change.append(char)
            elif char == next_char:
                if min_ops == min_ops[ord(next_char) - ord('a')]:
                    chars_to_change.append(char)
            else:
                if min_ops == min(min_ops[ord(prev_char) - ord('a')] + 1, min_ops[ord(next_char) - ord('a')] + 1):
                    chars_to_change.append(char)

        # If there are no characters that need to be changed, return the original caption
        if not chars_to_change:
            return caption

        # Sort the characters that need to be changed in lexicographical order
        chars_to_change.sort()

        # Create the new caption by replacing the characters that need to be changed with their next character
        new_caption = ""
        for i in range(n):
            char = caption[i]
            if char in chars_to_change:
                new_caption += chr((ord(char) - ord('a') + 1) % 26 + ord('a'))
            else:
                new_caption += char

        return new_caption

