        dp = [[0] * (target_sum + 1) for _ in range(n + 1)]

        for i in range(n + 1):
            for j in range(target_sum + 1):
                if i == 0:
                    dp[i][j] = 0
                elif j == 0:
                    dp[i][j] = 1
                elif nums[i - 1] <= j:
                    dp[i][j] = dp[i - 1][j] + dp[i - 1][j - nums[i - 1]]
                else:
                    dp[i][j] = dp[i - 1][j]

        return dp[n][target_sum]

class Solution:
    def countPartitions(self, nums: List[int]) -> int:
        """
        :type nums: List[int]
        :rtype: int
        """
        total_sum = sum(nums)
        if total_sum % 2 != 0:
            return 0

        target_sum = total_sum // 2
        dp = [0] * (target_sum + 1)
        dp[0] = 1

        for num in nums:
            for i in range(target_sum, num - 1, -1):
                dp[i] += dp[i - num]

        return dp[target_sum]

class Solution:
    def countPartitions(self, nums: List[int]) -> int:
        """
        Counts the number of partitions where the difference between the sum of the left and right subarrays is even.

        :param nums: List[int] - The input list of integers.
        :return: int - The count of valid partitions.
        """
        n = len(nums)
        total_sum = sum(nums)
        even_count = 0
        odd_count = 0

        for num in nums:
            if num % 2 == 0:
                even_count += 1
            else:
                odd_count += 1

        if total_sum % 2 == 0:
            return (even_count * (even_count - 1) // 2) + (odd_count * (odd_count - 1) // 2)
        else:
            return (even_count * (even_count - 1) // 2)

