class Solution:
    def largestInteger(self, nums: List[int], k: int) -> int:
        """
        Finds the largest integer that appears in exactly one subarray of size k.

        :param nums: List of integers
        :param k: Size of the subarray
        :return: Largest integer that appears in exactly one subarray of size k, or -1 if no such integer exists
        """
        from collections import defaultdict

        # Count the frequency of each number
        num_count = defaultdict(int)
        for num in nums:
            num_count[num] += 1

        # Sort the numbers in descending order of their frequency
        sorted_nums = sorted(num_count.keys(), key=lambda x: num_count[x], reverse=True)

        # Create a dictionary to store the largest number that appears in each subarray of size k
        largest_nums = {}

        # Iterate over the sorted numbers
        for num in sorted_nums:
            # Check if the number appears in exactly one subarray of size k
            if num_count[num] == 1:
                # Find the largest number that appears in the current subarray of size k
                largest_num = -1
                for i in range(len(nums) - k + 1):
                    subarray = nums[i:i+k]
                    if num in subarray:
                        if largest_num == -1 or num > largest_num:
                            largest_num = num
                # Store the largest number in the dictionary
                largest_nums[num] = largest_num

        # Find the largest number that appears in exactly one subarray of size k
        largest_num = -1
        for num in sorted_nums:
            if num in largest_nums:
                if largest_num == -1 or num > largest_num:
