class Solution:
    def zigzagTraversal(self, grid: List[List[int]]) -> List[int]:
        """
        :type grid: List[List[int]]
        :rtype: List[int]
        """
        m, n = len(grid), len(grid[0])
        result = []
        i, j = 0, 0
        direction = 1  # 1 for right, -1 for left

        while i < m and j < n:
            result.append(grid[i][j])
            if direction == 1:
                if j == n - 1 or (i + 1 < m and grid[i + 1][j + 1] is None):
                    direction = -1
                    i += 1
                else:
                    j += 1
            else:
                if i == m - 1 or (j - 1 >= 0 and grid[i + 1][j - 1] is None):
                    direction = 1
                    j -= 1
                else:
                    i += 1

        return result
