    def zigzagTraversal(self, grid: List[List[int]]) -> List[int]:
        """
        :type grid: List[List[int]]
        :rtype: List[int]
        """
        result = []
        row, col = 0, 0
        direction = 1  # 1 for right, -1 for left

        while row < len(grid) and col < len(grid[0]):
            result.append(grid[row][col])
            if direction == 1:
                if col == len(grid[0]) - 1 or (row + 1 < len(grid) and grid[row + 1][col + 1] is None):
                    direction = -1
                    row += 1
                else:
                    col += 1
            else:
                if row == len(grid) - 1 or (col - 1 >= 0 and grid[row + 1][col - 1] is None):
                    direction = 1
                    col -= 1
                else:
                    row += 1

        return result

