class Solution:
    def minOperations(self, nums: List[int], x: int, k: int) -> int:
        """
        :type nums: List[int]
        :type x: int
        :type k: int
        :rtype: int
        """
        n = len(nums)
        if n < k * x:
            return -1

        prefix_sums = [0] * (n + 1)
        for i in range(n):
            prefix_sums[i + 1] = prefix_sums[i] + nums[i]

        min_operations = float('inf')
        for i in range(n - k * x + 1):
            target = prefix_sums[i + k * x] - prefix_sums[i]
            if target == 0:
                min_operations = min(min_operations, n - i - k * x)
            elif target > 0:
                left = i
                right = n - 1
                while left <= right:
                    mid = (left + right) // 2
                    if prefix_sums[mid + k * x] - prefix_sums[mid] == target:
                        min_operations = min(min_operations, n - i - k * x - (mid - i))
                        break
                    elif prefix_sums[mid + k * x] - prefix_sums[mid] < target:
                        left = mid + 1
                    else:
                        right = mid - 1

        return min_operations if min_operations != float('inf') else -1
